mapper.select:
SELECT COUNT(1) FROM (SELECT 9001 AS in_type, k1.`cas`, k1.`sku`, t.purchase_id, t.supplier_id, t.stock_status, d.dict_name AS stock_status_name, t.purchase_name, t.all_total, t.purchase_time, t2.staff_name, t3.`name` AS supplier_name FROM material_purchase t LEFT JOIN staff_info t2 ON (t.staff_id = t2.staff_id) LEFT JOIN cus_supplier_info t3 ON (t.supplier_id = t3.cs_sup_id) LEFT JOIN pss_dict d ON (t.stock_status = d.dict_id) LEFT JOIN material_purchase_detail k ON k.`purchase_id` = t.`purchase_id` LEFT JOIN product_material_info k1 ON k1.`product_id` = k.`product_id` WHERE t.purchase_isdel = 0 UNION ALL SELECT 7003 AS in_type, k3.cas, k3.sku, p1.research_id, '', p1.status, p2.dict_name AS stock_status_name, p1.research_name, p1.amount, p1.research_time, p3.staff_name, '' FROM product_research p1 LEFT JOIN pss_dict p2 ON (p1.status = p2.dict_id) LEFT JOIN staff_info p3 ON (p3.staff_id = p1.research_staff) LEFT JOIN product_material_info k3 ON k3.`product_id` = p1.`product_id` WHERE p1.research_is_del = 0 UNION ALL SELECT 9002 AS in_type, k5.cas, k5.sku, t1.sale_id, t1.customer_id, t3.stock_status, d2.dict_name AS stock_status_name, t1.sale_name, t1.all_total, t3.return_time, sf.staff_name, cu.`name` AS supplier_name FROM product_sale t1 LEFT JOIN product_sale_return t3 ON (t3.sale_id = t1.sale_id) LEFT JOIN pss_dict d2 ON (t3.stock_status = d2.dict_id) LEFT JOIN cus_supplier_info cu ON (cu.cs_sup_id = t1.customer_id) LEFT JOIN staff_info sf ON (sf.staff_id = t1.staff_id) LEFT JOIN product_sale_return_detail k4 ON k4.r_sale_id = t3.`r_sale_id` LEFT JOIN product_material_info k5 ON k5.product_id = k4.product_id WHERE t1.sale_isdel = 0) t1 WHERE t1.stock_status IS NOT NULL
and t1.in_type = ${params.in_type}
and (t1.stock_status =${params.stock_status})
and ${params.time}
AND (t1.cas like '%${params.search}%' or t1.sku like '%${params.search}%' or staff_name like '%${params.search}%')
AND t1.purchase_time between '${params.start_time}' and date_add('${params.end_time}',interval 1 day)

 CALL PRO_VENDITION_IN_INVENTORY(
#{P_SALE_ID, mode=IN, jdbcType=INTEGER},
#{P_BATCH_NO, mode=IN, jdbcType=NVARCHAR},
#{P_DESC, mode=IN, jdbcType=NVARCHAR},
#{P_AMOUNT, mode=IN, jdbcType=INTEGER}
)
