mapper.select:
SELECT out_type, CASE out_type WHEN 9001 THEN '采购退货' WHEN 9002 THEN '销售出库' WHEN 9003 THEN '研发领用' END AS out_type_str, name_ch, name_en, cas, sku, r_purchase_id, amount, unit, stock_status, stock_status_name, staff_name, staff_id, staff_name_return, staff_id_return, purchase_id, purchase_time, return_time, supplier_id, supplier_name FROM (SELECT 9001 AS out_type, k2.name_ch, k2.name_en, k2.cas, k2.sku, t1.r_purchase_id, k1.amount, k1.unit, t1.stock_status, t6.dict_name AS stock_status_name, t4.staff_name AS staff_name, t4.staff_id, t3.staff_name AS staff_name_return, t3.staff_id AS staff_id_return, t2.purchase_id, t2.purchase_time, t1.return_time, t5.cs_sup_id AS supplier_id, t5.NAME AS supplier_name FROM material_purchase_return t1 LEFT JOIN material_purchase t2 ON (t1.purchase_id = t2.purchase_id) LEFT JOIN staff_info t3 ON (t1.staff_id = t3.staff_id) LEFT JOIN staff_info t4 ON (t2.staff_id = t4.staff_id) LEFT JOIN cus_supplier_info t5 ON (t2.supplier_id = t5.cs_sup_id) LEFT JOIN pss_dict t6 ON (t1.stock_status = t6.dict_id) LEFT JOIN material_purchase_return_detail k1 ON k1.r_purchase_id = t1.r_purchase_id LEFT JOIN product_material_info k2 ON k2.product_id = k1.product_id WHERE t1.return_isdel = 0 UNION ALL SELECT 9003 AS out_type, k3.name_ch, k3.name_en, k3.cas, k3.sku, tt1.use_id, tt1.use_amount, tt1.use_unit, tt1.status, tt3.dict_name, '', 0, tt2.staff_name, tt2.staff_id, tt1.use_id, '', tt1.use_time, tt1.sale_d_id, tt1.is_sale FROM product_use tt1 LEFT JOIN staff_info tt2 ON (tt2.staff_id = tt1.use_staff) LEFT JOIN pss_dict tt3 ON (tt1.status = tt3.dict_id) LEFT JOIN product_material_info k3 ON k3.product_id = tt1.product_id WHERE tt1.use_is_del = 0 AND tt1.status NOT IN(11002,11003,11004,5001,5002,5003) UNION ALL SELECT 9002 AS out_type, k6.name_ch, k6.name_en, k6.cas, k6.sku, p1.sale_id, k5.amount, k5.unit, p1.`status`, p3.dict_name, '', 0, p2.staff_name, p2.staff_id, p1.sale_id, '', p1.sale_time, p4.supplier_id, p4.`name` AS supplier_name FROM product_sale p1 LEFT JOIN staff_info p2 ON (p2.staff_id = p1.staff_id) LEFT JOIN pss_dict p3 ON (p1.`status` = p3.dict_id) LEFT JOIN cus_supplier_info p4 ON (p1.customer_id = p4.cs_sup_id) LEFT JOIN product_sale_detail k5 ON k5.sale_id = p1.sale_id LEFT JOIN product_material_info k6 ON k6.product_id = k5.product_id WHERE p1.sale_isdel = 0) t1 WHERE t1.r_purchase_id != 0
and t1.stock_status =#{params.inventory_type}
AND t1.cas LIKE '%${params.cas}%'
AND t1.sku LIKE '%${params.sku}%'
and t1.stock_status in(${params.status_id})
and ${params.time}
and t1.out_type = ${params.out_type}
AND (t1.cas like '%${params.search}%' or t1.sku like '%${params.search}%' or staff_name like '%${params.search}%')
AND t1.return_time between '${params.start_time}' and date_add('${params.end_time}',interval 1 day)
ORDER BY t1.return_time DESC

SELECT COUNT(1) FROM (SELECT 9001 AS out_type, k2.cas, k2.sku, t1.r_purchase_id, t2.purchase_name, t1.stock_status, t6.dict_name AS stock_status_name, t4.staff_name AS staff_name, t4.staff_id, t3.staff_name AS staff_name_return, t3.staff_id AS staff_id_return, t2.purchase_id, t2.purchase_time, t1.return_time, t5.cs_sup_id AS supplier_id, t5.NAME AS supplier_name FROM material_purchase_return t1 LEFT JOIN material_purchase t2 ON (t1.purchase_id = t2.purchase_id) LEFT JOIN staff_info t3 ON (t1.staff_id = t3.staff_id) LEFT JOIN staff_info t4 ON (t2.staff_id = t4.staff_id) LEFT JOIN cus_supplier_info t5 ON (t2.supplier_id = t5.cs_sup_id) LEFT JOIN pss_dict t6 ON (t1.stock_status = t6.dict_id) LEFT JOIN material_purchase_return_detail k1 ON k1.r_purchase_id=t1.r_purchase_id LEFT JOIN product_material_info k2 ON k2.product_id=k1.product_id WHERE t1.return_isdel = 0 UNION ALL SELECT 9003 AS out_type, k3.cas, k3.sku, tt1.use_id, tt1.use_name, tt1.status, tt3.dict_name, '', 0, tt2.staff_name, tt2.staff_id, tt1.use_id, '', tt1.use_time, tt1.sale_d_id, tt1.is_sale FROM product_use tt1 LEFT JOIN staff_info tt2 ON (tt2.staff_id = tt1.use_staff) LEFT JOIN pss_dict tt3 ON (tt1.status = tt3.dict_id) LEFT JOIN product_material_info k3 ON k3.product_id=tt1.product_id WHERE tt1.use_is_del = 0 AND tt1.status NOT IN(11002,11003,11004,5001,5002,5003) UNION ALL SELECT 9002 AS out_type, k6.cas, k6.sku, p1.sale_id, p1.sale_name, p1.`status`, p3.dict_name, '', 0, p2.staff_name, p2.staff_id, p1.sale_id, '', p1.sale_time, p4.supplier_id, p4.`name` AS supplier_name FROM product_sale p1 LEFT JOIN staff_info p2 ON (p2.staff_id = p1.staff_id) LEFT JOIN pss_dict p3 ON (p1.`status` = p3.dict_id) LEFT JOIN cus_supplier_info p4 ON (p1.customer_id = p4.cs_sup_id) LEFT JOIN product_sale_detail k5 ON k5.sale_id=p1.sale_id LEFT JOIN product_material_info k6 ON k6.product_id=k5.product_id WHERE p1.sale_isdel = 0) t1 WHERE t1.r_purchase_id != 0
and t1.stock_status =#{params.inventory_type}
AND t1.cas LIKE '%${params.cas}%'
AND t1.sku LIKE '%${params.sku}%'
and t1.stock_status in(${params.status_id})
and ${params.time}
and t1.out_type = ${params.out_type}
AND (t1.cas like '%${params.search}%' or t1.sku like '%${params.search}%' or staff_name like '%${params.search}%')
AND t1.return_time between '${params.start_time}' and date_add('${params.end_time}',interval 1 day)
